package Revision;

public class zz {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i < 10; i++) {
			for (int j = 10; j > 0; j--) {
				
				if (j<=i) {
					System.out.print("*");
				}
				else {
					System.out.println(" ");
				}
			}
			System.out.println();
		}
	}

}
